//瞪羹 裘 斜葬晦
void drawMap(int* x, int* y)
{
	//萌濰 む檜雖 蟾晦
	scr_clear();
	//纔舒葬 斜葬晦
	border();
	drawInfo();
	gotoxy(MAPXSTART, 1);
	//Ы溯檜橫 晦遽 x高 譆模, 譆渠 | y高 譆模 譆渠 唸薑
	int hLow, hHigh, wLow, wHigh;

	//嬪, 嬴楚, 豭薹, 螃艇薹 部戲煎 鬲擊陽 濛嬴霞 睡碟虜躑 奩渠薹 裘 觼晦 棺葬晦
	int hUpDif = 0, hDownDif = 0, wLeftDif = 0, wRightDif = 0;
	int i = 1;

	//裘曖 譆模, 譆渠高 撲薑
	if (*y - MAPYHALF < 0) {
		hLow = 0;
		hUpDif = 0 - (*y - MAPYHALF);
	}
	else
		hLow = *y - MAPYHALF;

	if (*y + MAPYHALF > tempMapY) {
		hHigh = tempMapY;
		hDownDif = (*y + MAPYHALF) - tempMapY;
		hLow = hLow - hDownDif;
	}
	else
		hHigh = *y + MAPYHALF + hUpDif;

	if (*x - MAPXHALF < 0) {
		wLow = 0;
		wLeftDif = 0 - (*x - MAPXHALF);
	}
	else
		wLow = *x - MAPXHALF;

	if (*x + MAPXHALF > tempMapX) {
		wHigh = tempMapX;
		wRightDif = (*x + MAPXHALF) - tempMapX;
		wLow = wLow - wRightDif;
	}
	else
		wHigh = *x + MAPXHALF + wLeftDif;

	//裘 斜葬晦
	for (int h = hLow; h <= hHigh; h++) {
		for (int w = wLow; w <= wHigh; w++) {
			char temp = tempMap[h][w];
			//綴夠
			if (temp == '0' || temp == '9') {
				printscr("  ");
			}
			//跨蝶攪 蝶ア雖羲
			else if (temp == 'o') {
				printscr("  ");
			}
			//漁1
			else if (temp == '1') {
				printscr("﹥");
			}
			else if (temp == '2') {
				textcolor(GREEN1, GREEN1);
				printscr("Ⅲ");
				textcolor(WHITE, BLACK);
			}
			else if (temp == '3') {
				textcolor(GRAY1, GRAY1);
				printscr("Ⅳ");
				textcolor(WHITE, BLACK);
			}
			else if (temp == '4') {
				textcolor(WHITE, WHITE);
				printscr("  ");
				textcolor(WHITE, BLACK);
			}
			else if (temp == '5') {
				textcolor(RED1, RED1);
				printscr("Ⅵ");
				textcolor(WHITE, BLACK);
			}
			else if (temp == '6') {
				textcolor(CYAN1, CYAN1);
				printscr("Ⅶ");
				textcolor(WHITE, BLACK);
			}
			//Ы溯檜橫 該葬
			else if (temp == 'P') {
				*x = w;
				*y = h;
				printscr("∞");
			}
			//Ы溯檜橫 跺鱔
			else if (temp == 'p') {
				*x = w;
				*y = h;
				printscr("≧");
			}
			//陛煎識憲
			else if (temp == 'w') {
				printscr("式");
			}
			//撮煎識憲
			else if (temp == 'h') {
				printscr("弛");
			}
			//憐勒識憲
			else if (temp == 's') {
				printscr("Ⅸ");
			}
			//跨蝶攪
			else if (temp == 'Q') {
				textcolor(CYAN1, BLACK);
				printscr("Ⅹ");
				textcolor(WHITE, BLACK);
			}
			else if (temp == 'q') {
				textcolor(RED1, BLACK);
				printscr("﹣");
				textcolor(WHITE, BLACK);
			}
			else if (temp == 't') {
				textcolor(GREEN1, BLACK);
				printscr("﹢");
				textcolor(WHITE, BLACK);
			}
			else if (temp == 'e') {
				textcolor(WHITE, RED1);
				printscr("∼");
				textcolor(WHITE, BLACK);
			}
			else if (temp == 'r') {
				textcolor(RED2, BLACK);
				printscr("﹦");
				textcolor(WHITE, BLACK);
			}
			//寡歇 嬴檜蠱
			else if (temp == 'z') {
				printscr("BR");
			}
			//AR 驕璽
			else if (temp == 'x') {
				printscr("AR");
			}
			//SG 驕璽
			else if (temp == 'c') {
				printscr("SG");
			}
			//SR 驕璽
			else if (temp == 'v') {
				printscr("SR");
			}
			//羹溘 蛹 嬴檜蠱
			else if (temp == 'n') {
				printscr("Ⅵ");
			}
			//睡憮雖朝 漁
			else if (temp == '!') {
				printscr("十");
			}
			//寡歇
			else if (temp == 'k') {
				printscr("〣");
			}
			//寡歇 ァ嫦 賅暮
			else if (temp == 'K') {
				printscr("〡");
			}
		}
		i++;
		gotoxy(MAPXSTART, i);
	}

	//萌濰, 擅濰 瞪
	scr_switch();
}

void drawInfo() {
	if (isReload == FALSE)
		printGun();
	else
		reloadGun();
	//羹溘 轎溘
	gotoxy(10, 28);
	printscr("羹溘 : ");
	for (int i = 0; i < maxHP; i++) {
		if (i < userHP)
			printscr("Ⅵ");
		else
			printscr("Ⅴ");
	}

	//陴擎 跨蝶攪
	gotoxy(10, 30);
	printscr("陴擎 瞳 : ");
	int remainMonster = remainMonsterCount;
	char* remainMonster_char = (char*)malloc(sizeof(char) * 4);
	_itoa(remainMonster, remainMonster_char, 10);
	printscr(remainMonster_char);

	//⑷營 鼠晦轎溘
	gotoxy(10, 32);
	printscr("⑷營鼠晦: ");
	switch (selectWeapon) {
	case HG:
		printscr("掏識");
		break;
	case AR:
		printscr("給問模識");
		break;
	case SG:
		printscr("憐勒");
		break;
	case SR:
		printscr("蝶釭檜ぷ");
		break;
	case BR:
		printscr("寡歇");
		break;
	}
	//陴擎識憲, 驕璽 轎溘
	gotoxy(10, 34);
	printscr("陴擎 識憲 : ");
	int weaponBullet = weapon[selectWeapon].bullet;

	char* weaponBullet_char = (char*)malloc(sizeof(char) * 4);
	_itoa(weaponBullet, weaponBullet_char, 10);
	printscr(weaponBullet_char);
	printscr(" / 驕璽 : ");

	int weaponMagazine = weapon[selectWeapon].magazine;
	char* weaponMagazine_char = (char*)malloc(sizeof(char) * 4);
	_itoa(weaponMagazine, weaponMagazine_char, 10);
	printscr(weaponMagazine_char);

	//營濰瞪 轎溘
	gotoxy(10, 36);
	if (isReload == TRUE)
		printscr("營濰瞪醞...");

}